mapper.select:
SELECT c.cid, c.cname, t.tid, t.tname, s.sid, s.sname, sct.grade, sct.term
FROM studentms.studentcourseteacher sct INNER JOIN studentms.student s ON sct.sid = s.sid
INNER JOIN studentms.teacher t ON sct.tid = t.tid
INNER JOIN studentms.course c ON sct.cid = c.cid


        s.sid = #{sid}


        AND t.tid = #{tid}


        AND c.cid = #{cid}



            AND s.sname = #{sname}


            AND s.sname LIKE CONCAT('%', #{sname}, '%')




            AND t.tname = #{tname}


            AND t.tname LIKE CONCAT('%', #{tname}, '%')




            AND c.cname = #{cname}


            AND c.cname LIKE CONCAT('%', #{cname}, '%')



        AND sct.term = #{term}


        AND sct.grade >= #{lowBound}


        AND #{highBound} >= sct.grade

SELECT c.cid, c.cname, t.tid, t.tname, s.sid, s.sname, sct.grade, sct.term
FROM studentms.studentcourseteacher sct INNER JOIN studentms.student s ON sct.sid = s.sid
INNER JOIN studentms.teacher t ON sct.tid = t.tid
INNER JOIN studentms.course c ON sct.cid = c.cid


        s.sid = #{sid}


        AND t.tid = #{tid}


        AND c.cid = #{cid}



            AND s.sname = #{sname}


            AND s.sname LIKE CONCAT('%', #{sname}, '%')




            AND t.tname = #{tname}


            AND t.tname LIKE CONCAT('%', #{tname}, '%')




            AND c.cname = #{cname}


            AND c.cname LIKE CONCAT('%', #{cname}, '%')



        AND sct.term = #{term}


        AND sct.grade >= #{lowBound}


        AND #{highBound} >= sct.grade





    AND sct.grade < 60.0


    AND sct.grade >= 60.0 AND sct.grade < 70.0


    AND sct.grade >= 70.0 AND sct.grade < 80.0


    AND sct.grade >= 80.0 AND sct.grade < 90.0


    AND sct.grade >= 90.0
